import React, { useState } from 'react';
import './index.css';

const api = {
  key: '6b3b27e4e94b25edf5e550e16274c142',
  base: 'https://api.openweathermap.org/data/2.5/',
};

const dateBuilder = (d) => {
  const months = ['January','February','March','April','May','June','July','August','September','October','November','December'];
  const days = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
  const day = days[d.getDay()];
  const date = d.getDate();
  const month = months[d.getMonth()];
  const year = d.getFullYear();
  return `${day} ${date} ${month}, ${year}`;
};

function App() {
  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState(null);

  const search = (e) => {
    if (e.key === 'Enter' && query.trim()) {
      fetch(`${api.base}weather?q=${encodeURIComponent(query)}&appid=${api.key}&units=metric`)
        .then((res) => res.json())
        .then((result) => {
          if (result.cod === 200) setWeather(result);
          else setWeather(null);
          setQuery('');
        });
    }
  };

  const isWarm = weather?.main && weather.main.temp > 16;

  const countryCode = weather?.sys?.country; // e.g. "US"
  const countryName = countryCode
    ? new Intl.DisplayNames([navigator.language || 'en'], { type: 'region' }).of(countryCode)
    : ''; // e.g. "United States"

  return (
    <div className={`app ${isWarm ? 'Warm' : ''}`}>
      <main>
        <div className="search-box">
          <input
            type="text"
            className="search-bar"
            placeholder="Search..."
            onChange={(e) => setQuery(e.target.value)}
            value={query}
            onKeyDown={search}
          />
        </div>

        {weather?.main ? (
          <>
            <div className="location-box">
              <div className="location">
                {weather.name}{countryCode ? `, ${countryCode}` : ''}
                {/* or use full name: {countryName ? `, ${countryName}` : ''} */}
              </div>
              <div className="date">{dateBuilder(new Date())}</div>
            </div>
            <div className="weather-box">
              <div className="temp">{Math.round(weather.main.temp)}Â°C</div>
              <div className="weather">{weather.weather?.[0]?.description}</div>
            </div>
          </>
        ) : null}
      </main>
    </div>
  );
}

export default App;
